import * as m3o from "@m3o/m3o-node";
export class AiService {
    constructor(token) {
        this.client = new m3o.Client({ token: token });
    }
    // Make a request to the AI
    complete(request) {
        return this.client.call("ai", "Complete", request);
    }
    // Edit or edit prompt/code
    edit(request) {
        return this.client.call("ai", "Edit", request);
    }
    // Generate an image from prompt
    generate(request) {
        return this.client.call("ai", "Generate", request);
    }
    // Moderate hate speech
    moderate(request) {
        return this.client.call("ai", "Moderate", request);
    }
}
